mapper.select:
select

from t_ha_performance_regulartime_record
where DELETE_STATUS = #{deleteStatus}

   AND WORK_USER_OID = #{workUserOid}


  AND CASE_WORKER_NAME = #{caseWorkerName}


  AND COMPANY_NAME = #{companyName}


  AND SERVICE_BEGIN_TIME = #{serviceBeginTime,jdbcType=TIMESTAMP}


  AND SERVICE_PEOPLE_NUM = #{servicePeopleNum,jdbcType=VARCHAR}

SELECT wu.NAME as WORK_USER_NAME,wu.ID as WORK_USER_OID, rr.MANAGE_FACTOR,sum(rr.SERVICE_PEOPLE_NUM) as SERVICE_PEOPLE_NUM,sum(rr.SERVICE_DURATION) as SERVICE_DURATION from t_ha_help_work_user wu LEFT JOIN t_ha_performance_regulartime_record rr on rr.WORK_USER_OID = wu.ID AND rr.DELETE_STATUS = '1'
AND rr.CREATE_DATE >= #{beginTime}
AND rr.CREATE_DATE <= #{endTime}
where wu.DELETE_STATUS = '1'
AND wu.NAME like concat('%', #{workUserName}, '%')
GROUP BY wu.ID

SELECT wu.NAME as WORK_USER_NAME,wu.ID as WORK_USER_OID, rr.MANAGE_FACTOR,sum(rr.SERVICE_PEOPLE_NUM) as SERVICE_PEOPLE_NUM,sum(rr.SERVICE_DURATION) as SERVICE_DURATION
from t_ha_help_work_user wu
LEFT JOIN t_ha_performance_regulartime_record rr
on rr.WORK_USER_OID = wu.ID
AND rr.DELETE_STATUS = '1'

  AND rr.CREATE_DATE >= #{beginTime}


  AND rr.CREATE_DATE <= #{endTime}

where wu.DELETE_STATUS = '1'

  AND wu.NAME like concat('%', #{workUserName}, '%')


  AND wu.GROUP_SPLIT_ID <= #{groupSplitId}


    AND wu.ID in

    #{item, jdbcType=VARCHAR}



GROUP BY wu.ID

SELECT pr.WORK_USER_OID,wu.NAME as WORK_USER_NAME,sum(pr.PLUS_DURATION) as PLUS_DURATION, pr.PLUS_PROJECT_OID, hp.NAME as PLUS_PROJECT_Name from t_ha_performance_plustime_record pr LEFT JOIN t_ha_help_work_user wu on pr.WORK_USER_OID = wu.ID LEFT JOIN t_ha_plus_project hp on pr.PLUS_PROJECT_OID = hp.ID where pr.DELETE_STATUS = '1' AND wu.DELETE_STATUS = '1' AND pr.AUDIT_STATUS = '1'
AND pr.CREATE_DATE >= #{beginTime}
AND pr.CREATE_DATE <= #{endTime}
AND hp.ID = #{plusProjectOid}
AND pr.WORK_USER_OID = #{workUserOid}
GROUP BY pr.WORK_USER_OID

SELECT pr.ID,pr.GROUP_LEADER_ID, pr.GROUP_LEADER_NAME,pr.AUDIT_STATUS, wu.PHONE groupLeaderPhone,wg.NAME groupName, count(pr.GROUP_LEADER_ID) noAuditStatusSum from t_ha_performance_plustime_record pr left JOIN t_ha_help_work_user wu on pr.GROUP_LEADER_ID = wu.ID left JOIN t_ha_work_user_group wg on wu.GROUP_ID = wg.ID WHERE pr.DELETE_STATUS = '1' AND wu.DELETE_STATUS = '1' AND wg.DELETE_STATUS = '1' AND wu.GROUP_POST = '1' AND pr.AUDIT_STATUS= '0'
AND pr.CREATE_DATE >= #{beginTime}
AND pr.CREATE_DATE <= #{endTime}
AND pr.GROUP_LEADER_ID = #{groupLeaderId}
GROUP BY pr.GROUP_LEADER_ID
