mapper.select:
select ID, SERVICE_LINK_OID, SERVICE_OID, LINK_NAME, LINK_OID, HANDLE_TIME, TIME_UNIT, SPECIAL_FLAG, CREATE_DATE, CREATE_USER, DEL_FLAG, LINK_SORT, SPECIAL_TIME, POST_OIDS, POST_NAMES, POST_DUTY, HANDLE_USER_OIDS, HANDLE_USER_NAMES, HANDLE_USER_PHONE, HANDLE_USER_QUALIFIED, SERVICE_DESCRIBE, SPECIAL_BASIS from t_sx_service_link where 1 = 1 and DEL_FLAG = 0 and SERVICE_OID = #{serviceOid,jdbcType=VARCHAR}
AND LINK_NAME = #{linkName,jdbcType=VARCHAR},
AND LINK_OID = #{linkOid,jdbcType=VARCHAR},
AND HANDLE_TIME = #{handleTime,jdbcType=DECIMAL},
AND TIME_UNIT = #{timeUnit,jdbcType=VARCHAR}
order by LINK_SORT ASC, CREATE_DATE DESC
