mapper.select:
select
  distinct
ID, CORRECTION_OID, CASE_OID,SERVICE_OID, APPLY_PROJECT_NAME, CASE_NUMBER, APPLY_USER_NAME, USER_PHONE,
ORGAN_OID, DUE_DATE, CORRECTION_USER_OID, CORRECTION_STATUS, CREATE_DATE,
CREATE_USER, PATCH_TIME_LIMIT, NOTICE_FLAG
from t_ql_case_correction
where  1 = 1

  AND CASE_OID = #{caseOid}


  AND NOTICE_FLAG = #{noticeFlag}


  AND CORRECTION_STATUS = #{correctionStatus}


  AND ORGAN_OID = #{organOid}


  AND APPLY_USER_NAME = #{registerUser}


  AND CASE_NUMBER = #{caseNumber}


  AND CREATE_DATE >= str_to_date( #{startDate},'%Y-%m-%d %H:%i:%s')


  AND CREATE_DATE <= str_to_date( #{endDate},'%Y-%m-%d %H:%i:%s')


  and SERVICE_OID in

    #{serviceOid}


order by CREATE_DATE desc
