mapper.select:
select

from t_notepad
WHERE date_format(CREATE_DATE,'%Y-%m') = #{time} and CREATE_USER_OID = #{createUserOid} and DEL_FLAG = '0' ORDER BY CREATE_DATE;

select DISTINCT (DATE_FORMAT(CREATE_DATE,'%Y-%m-%d'))as time
from t_notepad
WHERE date_format(CREATE_DATE,'%Y-%m') = #{time} and CREATE_USER_OID = #{createUserOid} and DEL_FLAG = '0' ORDER BY time;
