mapper.insert:

insert into t_sys_login (USER_OID, LOGIN_OID,
  ACCOUNT, PASSWORD, NAME,
  FAIL_NUM, LOCK_STATUS, IS_ABLE,
  IS_DELETE, LOCK_DATE, MODIFY_DATE, PASSWORD_DATE
  )
values (#{userOid,jdbcType=VARCHAR}, #{loginOid,jdbcType=VARCHAR},
  #{account,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR},
  #{failNum,jdbcType=INTEGER}, #{lockStatus,jdbcType=INTEGER}, #{isAble,jdbcType=INTEGER},
  #{isDelete,jdbcType=INTEGER}, #{lockDate,jdbcType=TIMESTAMP}, #{modifyDate,jdbcType=TIMESTAMP}, #{passwordDate,jdbcType=TIMESTAMP}
  )

mapper.select:
select syslogin.ID as id, syslogin.USER_OID as userOid, syslogin.ACCOUNT as account,syslogin.LOCK_STATUS lockStatus,
syslogin.IS_ABLE as isAble,usr.name userName,d.DISTRICT_OID as districtOid, d.name as districtName ,o.ORGAN_OID as organOid,o.name as organName
from t_sys_login syslogin LEFT JOIN t_sys_user usr ON syslogin.USER_OID =usr.USER_OID
LEFT JOIN t_sys_district d ON d.DISTRICT_OID = usr.DISTRICT_OID
LEFT JOIN t_sys_organ o ON o.ORGAN_OID = usr.ORGAN_OID  and o.IS_DELETE=0 where 1=1
and usr.IS_DELETE=0  and d.IS_DELETE=0 and usr.IS_ABLE=1 and d.IS_ABLE=1

  and d.DISTRICT_OID = #{districtOid,jdbcType=VARCHAR}


  and o.ORGAN_OID = #{organOid,jdbcType=VARCHAR}


  and syslogin.account = #{account,jdbcType=VARCHAR}


  and usr.name LIKE #{userName,jdbcType=VARCHAR}


  and syslogin.LOCK_STATUS = #{lockStatus,jdbcType=INTEGER}


  and d.DISTRICT_OID_PATH LIKE #{loginDistrictOid,jdbcType=VARCHAR}

order by syslogin.id desc
